function[valeur] = phir_delta_delta(delta,tau)
load constantes

valeur = sum(n_i(1:7).*d_i(1:7).*(d_i(1:7)-1).*delta.^(d_i(1:7)-2).*tau.^(t_i(1:7))) +...
         sum(n_i(8:34).*exp(-delta.^(c_i(8:34))).*(delta.^(d_i(8:34)-2).*tau.^(t_i(8:34)).*((d_i(8:34)-c_i(8:34).*delta.^(c_i(8:34))).*(d_i(8:34)-1-c_i(8:34).*delta.^(c_i(8:34)))-(c_i(8:34)).^(2).*delta.^(c_i(8:34))))) +...
