         2.*A_i(40:42).^2.*(1./beta_i(40:42)).^2.*(((delta-1).^2).^(1./(2.*beta_i(40:42))-1).^2 )  +  A_i(40:42).*((1-tau)+ A_i(40:42) .* (  ((delta-1).^2).^(  1./(2.*beta_i(40:42))  ) )).*(4./beta_i(40:42)).*(1./(2.*beta_i(40:42))-1).*((delta-1).^2).^(1./(2.*beta_i(40:42))-2)   )) +...
         (b_i(40:42)-1).*(DELTA(delta,tau)).^(b_i(40:42)-2).*((delta-1)  .*  (     A_i(40:42).*((1-tau)+ A_i(40:42) .* (  ((delta-1).^2).^(  1./(2.*beta_i(40:42))  ) )).*( 2./beta_i(40:42) ).*( ((delta-1).^2).^((1./2.*beta_i(40:42))-1) ) +...
         2.*B_i(40:42).*a_i(40:42).*( (delta-1).^2 ).^(a_i(40:42)-1)    )).^2  )).*delta.*(PSI(delta,tau) )));

